package day1.basicprogram;

public class defaultConst {
	byte b;
	short s;
	int age;
	long l;
	float f;
	double salary;
	char c_var;
	boolean bl;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		defaultConst c1=new defaultConst();
		
		System.out.println("Byte Default value :"+c1.b);
		System.out.println("Short Default value :"+c1.s);
		System.out.println("int Default value :"+c1.age);
		System.out.println("long Default value :"+c1.l);
		System.out.println("float Default value :"+c1.f);
		System.out.println("Double Default value :"+c1.salary);
		System.out.println("char Default value :"+c1.c_var);
		System.out.println("boolean Default value :"+c1.bl);
		
	}

}
