package day1.basicprogram;

public class ifCondition3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=50, num2=30;
		
		if(num1!=num2)
		{
				System.out.println(" Number one is not equal to number2 ");
		}
		else
		{
			    System.out.println(" Number one is equal number2 ");
		}
		
		if(num1>num2)
		{
				System.out.println(" Number one is greater than number2 ");
		}
		else
		{
			    System.out.println(" Number one is smaller than number2 ");
		}
		
		if(num1<=num2)
		{
				System.out.println(" Number one is less than or equal to number2 ");
		}
		else
		{
			    System.out.println(" Number one is not less than or equal to number2 ");
		}
		
		if(num1==num2 && num1>num2)
		{
				System.out.println(" Number one is greater than or euqal to number2 ");
		}
		else
		{
			    System.out.println(" Number one is greater than or equal to than number2 ");
		}
		

	}

}
